import streamlit as st
st.title("The bank name is Unkown")
st.image("data:image/png;base64,")
name = st.text_input("What is your name:  ")
mh = st.number_input("How much money do you have:  ",-3)
mp = st.number_input("How much money have you spent",1)
ml = (mh - mp)
if st.button("Show how much i have left"):
  st.write(f"{name} you have ${ml} left")


